import React from 'react';
import Link from 'next/link';

const Pesquisa = () =>{
const save = async () =>{
   const form = {
       Nome : '111',
       Email: '222',
       Whatsapp: '333'
   }
 const response = await fetch('api/save',{
       method: 'POST',
       body: JSON.stringify(form)
   })

   const data = await response.json()
   console.log(data)
}

    return (
      <div className='pt-6'>
      <div className="mt12 text-center">
          <p>Criticas e sugest√µes.</p>
      </div>
      <div className="w-1/5 mx-auto">
          <label className='font-bold m-2'>Seu nome:</label>
          <input type="text" className='p-4 block shadow bg-blue-100 m-2 rouded'></input>
          <button className='bg-yellow-400 px-6 py-4 font-bold rounded-lg shadow-lg block m-2'
                  onClick={save}>Enviar resposta</button>
      </div>
      <div className="mt12 text-center">
          <p>Mensagem desconto.</p>
      </div>
  </div>
            );
}

export default Pesquisa
